.segment	"CODE"

.proc	_movement: near

.segment	"CODE"

;
; if(pad1 & PAD_LEFT) {
;
	lda     _pad1
	and     #$02
	beq     L000A
;
; Snake1.x -= 1;
;
	dec     _Snake1
;
; Snake1.head_sprite = SNAKE_HEAD_TILE_LEFT;
;
	lda     #$00
;
; else if (pad1 & PAD_RIGHT){
;
	jmp     L0008
L000A:	lda     _pad1
	and     #$01
	beq     L000B
;
; Snake1.x += 1;
;
	inc     _Snake1
;
; Snake1.head_sprite = SNAKE_HEAD_TILE_RIGHT;
;
	lda     #$03
L0008:	sta     _Snake1+5
;
; if(pad1 & PAD_UP){
;
L000B:	lda     _pad1
	and     #$08
	beq     L000C
;
; Snake1.y -= 1;
;
	dec     _Snake1+1
;
; Snake1.head_sprite=SNAKE_HEAD_TILE_UP;
;
	lda     #$01
;
; else if (pad1 & PAD_DOWN){
;
	jmp     L0009
L000C:	lda     _pad1
	and     #$04
	beq     L0007
;
; Snake1.y += 1;
;
	inc     _Snake1+1
;
; Snake1.head_sprite=SNAKE_HEAD_TILE_DOWN;
;
	lda     #$02
L0009:	sta     _Snake1+5
;
; } 
;
L0007:	rts

.endproc

