;
	sta     _mouse1+10
;
; Snake1.ate =1;
;
	sta     _Snake1+7
;
; return 1;
;
	ldx     #$00
	rts
;
; return 0;
;
L0002:	ldx     #$00
	txa
;
; }
;
	rts

.endproc

; ---------------------------------------------------------------
; void __near__ draw_mouse (void)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_draw_mouse: near

.segment	"CODE"

;
; if(mouse1.respawn =1){
;
	lda     #$01
	sta     _mouse1+10
;
; oam_spr(mouse1.mouse_x, mouse1.mouse_y, mouse1.mouse_spr,mouse1.sprite_att );
;
	jsr     decsp3
	lda     _mouse1
	ldy     #$02
	sta     (sp),y
	lda     _mouse1+1
	dey
	sta     (sp),y
	lda     _mouse1+7
	dey
	sta     (sp),y
	lda     _mouse1+8
	jsr     _oam_spr
;
; mouse1.mouse_x+=mouse1.mouse_dx;
;
	lda     _mouse1+4
	clc
	adc     _mouse1
	sta     _mouse1
;
; mouse1.mouse_y+=mouse1.mouse_dy;
;
	lda     _mouse1+5
	clc
	adc     _mouse1+1
	sta     _mouse1+1
;
; if(mouse1.mouse_x>=(256-8)) mouse1.mouse_dx=-mouse1.mouse_dx;
;
	lda     _mouse1
	cmp     #$F8
	bcc     L0005
	lda     _mouse1+4
	eor     #$FF
	clc
	adc     #$01
	sta     _mouse1+4
;
; if(mouse1.mouse_y>=(240-8)) mouse1.mouse_dy=-mouse1.mouse_dy;
;
L0005:	lda     _mouse1+1
	cmp     #$E8
	lda     #$00
	bcc     L0007
	lda     _mouse1+5
	eor     #$FF
	clc
	adc     #$01
	sta     _mouse1+5
;
; mouse1.respawn =0;
;
	lda     #$00
L0007:	sta     _mouse1+10
;
; }
;
	rts

.endproc

